window.addEventListener("scroll",()=>{const t=document.querySelector(".background-img");let e=window.pageYOffset;window.innerWidth>=750&&(t.style.transform="translateY("+.6*e+"px)")});let tl=gsap.timeline({duration:2,scrollTrigger:{trigger:".background",start:"100px top",end:"bottom bottom",scrub:!0,markers:!1}});window.addEventListener("scroll",()=>{tl.to(".header-container",{x:"50vw"})});